mapper.select:
SELECT
p.*,
(SELECT AVG(REVSTAR) FROM REVIEW r WHERE r.PRDNO = p.PRDNO) AS avgRating,
(SELECT COUNT(*) FROM REVIEW r WHERE r.PRDNO = p.PRDNO) AS reviewCount,
(
SELECT COUNT(*)
FROM REVIEW r
INNER JOIN USERS u ON u.userNo = r.userNo
INNER JOIN REVIEW_FEEDBACK rf ON rf.revNo = r.revNo
WHERE r.prdNo = p.prdNo
AND rf.rfTypeCode < 500


    AND u.userSkin = #{skin}



    AND p.prdSubCate = #{subcate}



    AND p.prdSubCate = #{sub_cate}

) AS cnt
FROM PRODUCT p
WHERE 1 = 1


    AND p.prdMainCate = #{maincate}



    AND p.prdSubCate = #{subcate}



    AND p.prdSubCate = #{sub_cate}


ORDER BY cnt DESC
FETCH FIRST #{limit} ROWS ONLY

SELECT p.prdNo, p.prdName, p.prdPrice, p.prdCompany, p.prdImg, p.prdMainCate, p.prdSubCate FROM PRODUCT p WHERE 1 = 1
AND p.prdMainCate = #{maincate}
AND p.prdSubCate = #{subcate}
AND p.prdPrice >= #{min_price}
AND p.prdPrice <= #{max_price}
AND p.prdCompany IN
#{b}
ORDER BY p.prdNo DESC

SELECT p.prdNo, p.prdName, p.prdPrice, p.prdCompany, p.prdImg, p.prdMainCate, p.prdSubCate FROM PRODUCT p WHERE 1=1
AND p.prdMainCate = #{maincate}
AND p.prdSubCate = #{subcate}
AND p.prdPrice >= #{min_price}
AND p.prdPrice <= #{max_price}
AND p.prdCompany IN
#{b}
AND EXISTS ( SELECT 1 FROM REVIEW r JOIN USERS u ON u.userNo = r.userNo WHERE r.prdNo = p.prdNo AND u.userSkin = #{skin} )
ORDER BY p.prdNo DESC
