mapper.select:
SELECT MAX(REVNO)
FROM REVIEW
WHERE USERNO = #{userNo}

mapper.update:
UPDATE REVIEW
SET REVSTAR = #{rating},
REVGOOD = #{good},
REVBAD = #{bad},
CONTENT = #{content}
WHERE USERNO = #{userNo}
AND REVNO = #{reviewId}

mapper.delete:
DELETE
FROM REVIEW
WHERE USERNO = #{userNo}
AND REVNO = #{reviewId}
